import React, { useState, useEffect } from "react";
import getallcoupons from "./getallcoupons";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import AddToCartCouponsAxios from "./AddToCartCouponsAxios";

function Coupons() {
const [allcoupons, setallcoupons] = useState([
]);
const [images, setimages] = useState([
]);

useEffect(() => {
getallcoupons.getallcoupons().then((value) => {
setallcoupons(value);
console.log(value);
});
getallimages.getallimages().then((value) => {
setimages(value);
console.log(value);
if (images.length === 0) {
alert("There are no images to show.");
}
});
}, []);

const cardItem = (item, image) => {
const addtocart = () => {
console.log(item.category);
let body = {
category: item.category,
offer: item.offer,
companyName: item.companyName,
price: item.price,
expiryDate: item.expiryDate,
};
AddToCartCouponsAxios.AddToCartCouponsAxios(body).then((value) => {
alert("item added to cart");
});
};

return (
  <Card className="my-5 py-4" style={{ width: "30rem" }}>
    <Card.Img src={`/assets/images/${image.imageUrl}`} />
    <Card.ImgOverlay>
      <Card.Body className="text-center my-5">
        <Card.Title>{item.offer}</Card.Title>
        <Card.Text>Category : {item.category}</Card.Text>
        <Card.Text>Company Name : {item.companyName}</Card.Text>
        <Card.Text>Price : ₹{item.price}</Card.Text>
        <Card.Text>Expiry Date : {item.expiryDate}</Card.Text>
        <Button onClick={addtocart} variant="primary">Add to cart</Button>
      </Card.Body>
    </Card.ImgOverlay>
  </Card>
);
};

return (
<Container id="coupons_all">
<Row className="justify-content-around">
{allcoupons.map((item, index) => (
<Col key={index}>
{images.find((image) => image.id === item.id) ? cardItem(item, images.find((image) => image.id === item.id)) : null}
</Col>
))}
</Row>
</Container>
);
}
